/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `consolidate_arrivals_date` AFTER INSERT ON `ilg_attendance`
 FOR EACH ROW BEGIN
  declare summaryId bigint(20);
  declare areaId bigint(20);
  declare areaName varchar(200);
  
  if NEW.attendance_status_id = 0 then
	
	  select c.id, c.name into areaId, areaName from ilg_person a inner join ilg_area_person b on b.person_id = a.id inner join ilg_area c on c.id = b.area_id where a.id = NEW.person_id;

	  if areaId is null then
		  Set summaryId =(select id from ilg_arrivals_date_summary where Date(time) = Date(NEW.time) and area_id is null);
	  else 
		  Set summaryId =(select id from ilg_arrivals_date_summary where Date(time) = Date(NEW.time) and area_id = areaId);
	  end if;


	  if summaryId is null then
		
		IF NEW.attendance_type_id = 0 then
			INSERT INTO ilg_arrivals_date_summary (time, area_id, area_name, total, late, on_time, early) values (Date(NEW.time), areaId, areaName, 1, 0, 1, 0);
		ELSEIF NEW.attendance_type_id = 1 THEN		
			INSERT INTO ilg_arrivals_date_summary (time, area_id, area_name, total, late, on_time, early) values (Date(NEW.time), areaId, areaName, 1, 0, 0, 1);
		ELSEIF NEW.attendance_type_id = 2 THEN		
				INSERT INTO ilg_arrivals_date_summary (time, area_id, area_name, total, late, on_time, early) values (Date(NEW.time), areaId, areaName, 1, 1, 0, 0);
		END IF;		

	  else
		IF NEW.attendance_type_id = 0 then
			UPDATE ilg_arrivals_date_summary SET on_time = on_time + 1, total = total + 1 WHERE id = summaryId;
		ELSEIF NEW.attendance_type_id = 1 THEN		
			UPDATE ilg_arrivals_date_summary SET early = early + 1, total = total + 1 WHERE id = summaryId;
		ELSEIF NEW.attendance_type_id = 2 THEN		
			UPDATE ilg_arrivals_date_summary SET late = late + 1, total = total + 1 WHERE id = summaryId;
		END IF;		
			
	  end if;
	end if;
  END */;;
